mapper.select:
SELECT p_contentno, title, content, file, file1, replycnt, money, meterial,  p_categoryno
FROM p_content
WHERE p_contentno=#{p_contentno}

mapper.update:
    UPDATE p_content
	SET title=#{title}, content=#{content}, file=#{file}, file1=#{file1}, money=#{money}, meterial=#{meterial}
	WHERE p_contentno= #{p_contentno}
